import React, { Component } from 'react';
import Chart from 'react-apexcharts';
import axios from "axios";


class HardwareTeste extends Component {

  constructor(props) {
    super(props);

    this.state = {
      options: {},
      series: [],
      labels: [],



    }
  }




  async getRequisicaoId() {
    const requisicao = await axios.get("http://localhost:1337/requisicaos");

    const data = requisicao.data;

    const options = data.map((e) => ({
      value: e.custo,

    }));

    this.setState({ series: options });
  }

  async getRequisicaoNome() {
    const requisicao = await axios.get("http://localhost:1337/requisicaos");

    const data = requisicao.data;

    const options = data.map((e) => ({
      label: e.equipamento.nome_equipamento,
    }));

    this.setState({ labels: options });
  }

  componentDidMount() {
    this.getRequisicaoId();
    this.getRequisicaoNome();
  }

  componentDidUpdate(prevProps, prevState) {
    if (this.state.options !== prevState.options) {
    }
  }
  render() {

    return (
      <>

        <div className="conteiner">
          <div className='row'>

            <div className='col-lg-4'>
              <div className="donut">
                <Chart options={this.state.options} series={this.state.series} type="donut" width="380" />
              </div>
            </div>
            <div className='col-lg-4'>
              {/* <div className="donut">
                <Chart options={this.state.options} series={this.state.RequisicaosID} type="donut" width="380" />
              </div> */}

            </div>
          </div>
        </div>
      </>
    );
  }
}

export default HardwareTeste;
