import React, { Component } from 'react';
import ReactApexChart from 'react-apexcharts';
import axios from "axios";

class Barra extends Component {

  constructor(props) {
    super(props);
    this.state = {
      series: [{
        data: [2, 2, 2, 2, 1, 3, 1]
      }, {
        data: [43, 32, 33, 52, 13, 44, 32]
      }],
      options: {
        labels: [],
        chart: {
          type: 'bar',
          height: 430
        },
        plotOptions: {
          bar: {
            horizontal: true,
            dataLabels: {
              position: 'top',
            },
          }
        },
        dataLabels: {
          enabled: true,
          offsetX: -6,
          style: {
            fontSize: '12px',
            colors: ['#fff']
          }
        },
        stroke: {
          show: true,
          width: 1,
          colors: ['#fff']
        },
        tooltip: {
          shared: true,
          intersect: false
        },
        xaxis: {
          categories: [],
        },
      },
  }
    
  }


  async getOptionsRequisicaos() {
    const equipamentos = await axios.get("http://localhost:1337/equipamentos");

    const data = equipamentos.data;

    const valores = data.reduce((acc,item)=>{
      	const exists = acc.find(element=>element.equipamento === item.nome_equipamento);
        if(exists){
          
          return acc;
        }
        acc.push({ equipamento: item.nome_equipamento})
        return acc;
    },[]);

    const valors = data.reduce((acc,item)=>{
      const exists = acc.find(element=>element.equipamento === item.status);
      if(exists){
        
        return acc;
      }
      acc.push({ equipamento: item.status})
      return acc;
  },[]);

  const stats = valors.map(v=>v.equipamento);
    const nome = valores.map(n=>n.equipamento);
    console.log(valores);
    console.log(valors);
    this.setState(prevState => ({
      ...prevState,
      options: {...prevState.options,xaxis:{categories: nome},labels: stats}
    }));


  } 

  componentDidMount(){
    this.getOptionsRequisicaos();
  } 

  

  componentDidUpdate(prevProps,prevState){
    if(prevState != this.state){
      console.log("a");
    }

  }
  render() {
    console.log(this.state.options.labels)
    return (
      
      <>

        <div className="conteiner">
          <div className='row'>

            <div className='col-lg-11'>
              
              <ReactApexChart options={this.state.options} series={this.state.series} type="bar" height={480} />
            </div>
          </div>
        </div>
      </>
    );
  }
}


export default Barra;
