import React, { Component } from 'react';
import Chart from 'react-apexcharts';
import axios from "axios";


class Hardware extends Component {

  constructor(props) {
    super(props);

    this.state = {
      options: {},
      series: [44, 55, 41, 17, 15],
      labels: ['A', 'B', 'C', 'D', 'E'],

      selectOptionsEquipamento: [],
      /* nome_equipamento: "", */
      id

    }
  }

  /* async getOptionsEquipamento() {
    const equipamento = await axios.get("localhost:1337/equipamentos");

    const data = equipamento.data;

    const options = data.map((e) => ({
      value: e.id,
      label: e.nome_equipamento,
    }));

    this.setState({ selectOptionsEquipamento: options });
  } */
  async getOptionsEquipamento() {
    const equipamento = await axios.get("localhost:1337/equipamentos");

    const data = equipamento.data;

    const options = data.map((e) => ({
      label: e.id,

    }));

    this.setState({ selectOptionsEquipamento: options });
  }

  async getOptionsEquipamento() {
    const equipamento = await axios.get("localhost:1337/equipamentos");

    const data = equipamento.data;

    const options = data.map((e) => ({
      label: e.nome_equipamento,
    }));

    this.setState({ selectOptionsEquipamento: options });
  }

  componentDidMount(){

  }

  render() {

    return (
      <>
        <div className="conteiner">
          <div className='row'>

            <div className='col-lg-4'>
              <div className="donut">
                <Chart options={this.state.options} series={this.state.series} type="donut" width="380" />
              </div>
            </div>
            <div className='col-lg-4'>
              <div className="donut">
                <Chart options={this.state.options} series={this.state.series} type="donut" width="380" />
              </div>

            </div>
          </div>
        </div>
      </>
    );
  }
}

export default Hardware;
