import React, { Component } from 'react';
import ReactApexChart from 'react-apexcharts';
import axios from "axios";

class Barra extends Component {

  constructor(props) {
    super(props);
    this.state = {
          
      series: [{
        data: [400, 430, 448, 470, 540, 580, 690, 1100, 1200, 1380]
      }],
      options: {
        chart: {
          type: 'bar',
          height: 350
        },
        plotOptions: {
          bar: {
            borderRadius: 4,
            horizontal: true,
          }
        },
        dataLabels: {
          enabled: false
        },
        xaxis: {
          categories: ['South Korea', 'Canada', 'United Kingdom', 'Netherlands', 'Italy', 'France', 'Japan',
            'United States', 'China', 'Germany'
          ],
        }
      },
    
    
    
  }
    
  }


  async getOptionsRequisicaos() {
    const requisicaos = await axios.get("http://localhost:1337/requisicaos");

    const data = requisicaos.data;

    const valores = data.reduce((acc,item)=>{
      	const exists = acc.find(element=>element.equipamento === item.equipamento.nome_equipamento);
        if(exists){
          exists.custo+=item.custo;
          return acc;
        }
        acc.push({custo: item.custo, equipamento: item.equipamento.nome_equipamento})
        return acc;
    },[])

    const custo = valores.map(a=>a.custo);
    const nome = valores.map(n=>n.equipamento);
    console.log(valores);
    this.setState(prevState => ({
      ...prevState,
      series: custo,
      options: {...prevState.options,labels: nome}
    }));


  } 

  componentDidMount(){
    this.getOptionsRequisicaos();
  } 

  

  componentDidUpdate(prevProps,prevState){
    if(prevState != this.state){
      console.log("a");
    }

  }
  render() {
    return (
      
      <>

        <div className="conteiner">
          <div className='row'>

            <div className='col-lg-4'>
              <div id="chart">
              <ReactApexChart options={this.state.options} series={this.state.series} type="bar" height={430} />
    
              </div>
            </div>
          </div>
        </div>
      </>
    );
  }
}


export default Barra;
