import React, { Component } from 'react';
import Chart from 'react-apexcharts';
import axios from "axios";


class Circle1 extends Component {

  constructor(props) {
    super(props);

    this.state = {
      options: {
        labels: [],
      },
      series: [],
    }
  }


  async getOptionsRequisicaos() {
    const requisicaos = await axios.get("http://localhost:1337/requisicaos");

    const data = requisicaos.data;
    console.log(data);
    const valores = data.map((e) => ({
      value: e.custo,
    }));
    console.log(valores)
    const equipamentos = data.map((e) => ({
      labels: e.equipamento.nome_equipamento,
    }));
    console.log(equipamentos);

    this.setState({ 
      series: valores ,
    labels: equipamentos});

    
    console.log(this.state.series);
    console.log(this.state.labels);
    
  }

  componentDidMount(){
    this.getOptionsRequisicaos();
  }

  render() {

    return (
      <>

        <div className="conteiner">
          <div className='row'>

            <div className='col-lg-4'>
              <div className="donut">
                <Chart options={this.state.options} series={this.state.series.value} type="donut" width="380" />
              </div>
            </div>
          </div>
        </div>
      </>
    );
  }
}

export default Circle1;
