import React, { Component } from 'react';
import ReactApexChart from 'react-apexcharts';
import axios from "axios";

class Barra extends Component {

  constructor(props) {
    super(props);
    this.state = {
      series: [{
        data: [44, 55, 41, 64, 22, 43, 21]
      }, {
        data: [53, 32, 33, 52, 13, 44, 32]
      }],
      options: {
        chart: {
          type: 'bar',
          height: 430
        },
        plotOptions: {
          bar: {
            horizontal: true,
            dataLabels: {
              position: 'top',
            },
          }
        },
        dataLabels: {
          enabled: true,
          offsetX: -6,
          style: {
            fontSize: '12px',
            colors: ['#fff']
          }
        },
        stroke: {
          show: true,
          width: 1,
          colors: ['#fff']
        },
        tooltip: {
          shared: true,
          intersect: false
        },
        xaxis: {
          categories: [2001, 2002, 2003, 2004, 2005, 2006, 2007],
        },
      },
  }
    
  }


  async getOptionsRequisicaos() {
    const equipamentos = await axios.get("http://localhost:1337/equipamentos");

    const data = equipamentos.data;

    const valores = data.reduce((acc,item)=>{
      	const exists = acc.find(element=>element.equipamento === item.nome_equipamento);
        if(exists){
          exists.custo+=item.custo;
          return acc;
        }
        acc.push({custo: item.custo, equipamento: item.equipamento.nome_equipamento})
        return acc;
    },[])

    const custo = valores.map(a=>a.custo);
    const nome = valores.map(n=>n.equipamento);
    console.log(valores);
    this.setState(prevState => ({
      ...prevState,
      series: custo,
      options: {...prevState.options,labels: nome}
    }));


  } 

  componentDidMount(){
    this.getOptionsRequisicaos();
  } 

  

  componentDidUpdate(prevProps,prevState){
    if(prevState != this.state){
      console.log("a");
    }

  }
  render() {
    return (
      
      <>

        <div className="conteiner">
          <div className='row'>

            <div className='col-lg-4'>
              <div className="donut">
              <ReactApexChart options={this.state.options} series={this.state.series} type="bar" height={430} />
    
              </div>
            </div>
          </div>
        </div>
      </>
    );
  }
}


export default Barra;
