import React, { Component } from 'react';
import Chart from 'react-apexcharts';
import axios from "axios";
import Circle1 from './Circle1';
import CustoReparacao from './CustoReparacao';
import Box from "@mui/material/Box";
import Select from "react-select";

class HardwareTeste extends Component {

  constructor(props) {
    super(props);
    this.state = {
      selectOptions: [],
    }
  }

  async getOptionsAgencia() {
    const res = await axios.get("http://localhost:1337/equipamentos");
    const data = res.data;

    const options = data.map((e) => ({
      value: e.agencia,
      label: e.agencia,
    }));

    this.setState({ selectOptions: options });
  }
  async handleChange(e) {
    this.setState({ currentAgencia: e.value });
  }

  componentDidMount() {
    this.getOptionsAgencia();
  }
  render() {

    return (
      <>

        <div className="conteiner">
          <div className='row'>
            <div className="col-lg-5">
              <Select
                options={this.state.selectOptions}
                id="praia"
                name="praia"
                onChange={this.handleChange.bind(this)}
              />
            </div>{" "}
            <br /> <br />
            <div className='col-lg-4'>
              Total Equipamento
            </div>
            <div className='col-lg-4'>
              Total Danificado
            </div>
            <div className='col-lg-4'>
              Custo Reparação
            </div>

            <div className='col-lg-7'>
              Tabela
            </div>
            <div className='col-lg-5'>
              <div className='col-lg-12'>

                <Box sx={{ width: 1 }}display="grid"
                    gridTemplateColumns="repeat(12, 1fr)"
                    gap={5} >
                  
                    
                 Custo Reparação por equipamento
                    {/* <Circle1/> */}
                    <CustoReparacao agenciaNome={this.state.currentAgencia} />
                  
                </Box>
              </div>
              <div className='col-lg-12'>
                Equipamento por estado
              </div>
            </div>
          </div>
        </div>
      </>
    );
  }
}

export default HardwareTeste;
