import React, { Component } from 'react';
import Chart from 'react-apexcharts';
import axios from "axios";


class Circle1 extends Component {

  constructor(props) {
    super(props);

    this.state = {
      options: {
        labels: ['A', 'B', 'C', 'D', 'E'],
      },
      series: [44, 55, 41, 7, 15],
    }
  }


  async getOptionsEquipamento() {
    const equipamento = await axios.get("http://localhost:1337/requisicaos");

    const data = equipamento.data;

    const valores = data.map((e) => ({
      value: e.custo,
    }));
    const equipamentos = data.map((e) => ({
      value: e.equipamentonome_equipamento,
    }));

    this.setState({ selectOptionsEquipamento: valores });
    console.log(this.state.selectOptionsEquipamento);
  }

  render() {

    return (
      <>

        <div className="conteiner">
          <div className='row'>

            <div className='col-lg-4'>
              <div className="donut">
                <Chart options={this.state.options} series={this.state.series} type="donut" width="380" />
              </div>
            </div>
          </div>
        </div>
      </>
    );
  }
}

export default Circle1;
